          bxtav(i,j,k)=bxtav(i,j,k) + bx(i,j,k,0)/nplot
          bytav(i,j,k)=bytav(i,j,k) + by(i,j,k,0)/nplot
          bztav(i,j,k)=bztav(i,j,k) + bz(i,j,k,0)/nplot
         endif
        enddo
       enddo
      enddo
      endif

