        call rhsdiv(ox(1-ih,1-ih,1,0),oy(1-ih,1-ih,1,0),
     .                                oz(1-ih,1-ih,1,0),rho,pfy,1)

!if (V5D == 1)
!       inbr=inbr+1 !=7
!       if (mype.eq.0) print *,'RHSDIV DONE'
!       call vis5d_out(u(1-ih,1-ih,1,0), v(1-ih,1-ih,1,0),
