C    *         pfx(i,j,k)  ,pfy(i,j,k)  ,pfz(i,j,k)
C     enddo
C     enddo
C     enddo
C     endif
CCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCC
#endif
#endif
#if (V5D == 1)
        call vis5d_out(u(1-ih,1-ih,1,0), v(1-ih,1-ih,1,0),
     *                 w(1-ih,1-ih,1,0),ox(1-ih,1-ih,1,0),
