      f1(i,j,k)=v1(i,j,k)
   48 v1(i,j,k)=0.

      julim = mp + topedge
      do 49 k=1,n3m
      do 49 j=1,julim
      do 49 i=1,np
      f2(i,j,k)=v2(i,j,k)
