      do 50 k=1,n3
      do 50 j=1,mp
      do 50 i=1,np
      f3(i,j,k)=v3(i,j,k)
   50 v3(i,j,k)=0.

c----------------------------------------------
compute antidiffusive velocities in x direction
c----------------------------------------------

      call update(x,np,mp,l,np,mp,iup)
#if (POLES == 0)
      if (rightedge.eq.0) then
         call update(f1,np,mp,l,np+1,mp,1)
      else
