      end if
#else
      call update(f1,np,mp,l,np+1,mp,1)
#endif
      if (topedge.eq.0) then
         call update(f2,np,mp,l,np,mp+1,1)
      else
         call update(f2,np,mp+1,l,np,mp+1,1)
      end if
      call update(f3,np,mp,l+1,np,mp,1)


#if (POLES == 0)
      jllim = 1  + botedge
      julim = mp - topedge
