     *            +abs(x(i-1,1,n3-2))+abs(x(i,1,n3-2))+ep,
     *             .5*(h(i-1,1,   1)+h(i,1,1)))
            v1(i, 1 ,n3m)=v1(i, 1 ,1)
#if (PARALLEL == 0)
            v1(i,n2m, 1 )=v1(i, 1 ,1)
            v1(i,n2m,n3m)=v1(i,n2m,1)
#endif
            enddo
        end if      !  botedge=1

#if (PARALLEL > 0)
        if (rightedge.eq.0) then
          call updatebt(v1,np,mp,l,np+1,mp,1)
        else
          call updatebt(v1,np+1,mp,l,np+1,mp,1)
