        if (topedge.eq.1) then
          do i=illim,np
            v1(i,mp, 1 )=v1(i,mp+1,1)
            v1(i,mp,n3m)=v1(i, mp ,1)
            end do
        end if
#endif

      endif   !ibcy=1
C----------------/
#endif /* POLES */
