     *    -vdiv2(f1(i,j,k),f3(i-1,j,k+1),f3(i,j,k+1),f3(i-1,j,k),
     *    f3(i,j,k),   .5*(h(i-1,j,k)+h(i,j,k)))

      endif

      if(isor.eq.3) then           !mod mhd
