      if(ibcx.eq.1) then
      if(leftedge.eq.1) then
       do k=2-ibcz,n3-2+ibcz
        do j=jllim,julim
      v1(2,j,k)=v1(2,j,k)     +vcor31(f1(2,j,k),
     1          x(-1,j,k),x(1,j,k),x(2,j,k),x(3,j,k),
     1                    .5*(h(1,j,k)+h(2,j,k)))
        end do
       end do
