     .          fqr(1-ih:nmsp+ih, 1-ih:mmsp+ih, lms),
     .         fqia(1-ih:nicp+ih, 1-ih:micp+ih, lic),
     .         fqib(1-ih:nicp+ih, 1-ih:micp+ih, lic)

      dimension tke(1-ih:nkvp+ih, 1-ih:mkvp+ih, lkv),
