     .         oy(1-ih:np+ih,1-ih:mp+ih,l),
     .         oz(1-ih:np+ih,1-ih:mp+ih,l),
c mod-restart
     .         ox2(1-ih:np+ih,1-ih:mp+ih,l),
     .         oy2(1-ih:np+ih,1-ih:mp+ih,l),
     .         oz2(1-ih:np+ih,1-ih:mp+ih,l),
