     .         fy(1-ih:np+ih,1-ih:mp+ih,l),
     .         fz(1-ih:np+ih,1-ih:mp+ih,l),
     .         ft(1-ih:np+ih,1-ih:mp+ih,l)
      dimension pm(1-ih:nmhdp+ih, 1-ih:mmhdp+ih, lmhd),
