      call mybarrier()
         call readitk(by,1,icomm)
      call mybarrier()
         call readitk(bz,1,icomm)
      call mybarrier()
         call readitk(fbx,1,icomm)
      call mybarrier()
         call readitk(fby,1,icomm)
      call mybarrier()
