      call mybarrier()
         call readitk(bz,0,icomm)
      call mybarrier()
         call readitk(fbx,0,icomm)
      call mybarrier()
         call readitk(fby,0,icomm)
      call mybarrier()
         call readitk(fbz,0,icomm)
      call mybarrier()
c mod-restart
         call readitk(bx1,0,icomm)
      call mybarrier()
         call readitk(by1,0,icomm)
      call mybarrier()
         call readitk(bz1,0,icomm)
      call mybarrier()
