      if (nke.eq.n) then
         call writeit0(ftke,temp,1,inr)
      call mybarrier()
      else
         call writeit0(ftke,temp,0,inr)
      call mybarrier()
      end if
      do jfil=1,nspc
      call writeit0( chm(1-ih,1-ih,1,jfil),temp,ichm,inr)
      call mybarrier()
      enddo
      do jfil=1,nspc
      call writeit0(fchm(1-ih,1-ih,1,jfil),temp,ichm,inr)
      call mybarrier()
      enddo

      if (ihis.eq.1) write(14) hise,hischm

      return
      end

