     .          tke(1-ih:nkvp+ih,1-ih:mkvp+ih,lkv),
     .         ftke(1-ih:nkep+ih,1-ih:mkep+ih,lke)
      dimension qia(1-ih:nicp+ih,1-ih:micp+ih,lic),
     .          qib(1-ih:nicp+ih,1-ih:micp+ih,lic),
     .         fqia(1-ih:nicp+ih,1-ih:micp+ih,lic),
