      subroutine updatebt(a,n1,m1,l1,ndim,mdim,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      dimension a(1-ih:ndim+ih, 1-ih:mdim+ih,l1)
#if (PARALLEL > 0)
#include "msg.lnk"
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif

#if (DIAGPLT == 1)
      print *,'UPDATEBT',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(73)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

      nhl=n1*ihr*l1
c
c-------------------------------------------------------------------
c     prepare bottom segments for processor below    :tmpxsnd1
c     prepare upper data segments for processor above:tmpxsnd2
c-------------------------------------------------------------------
c
      icnt=1
      do k=1,l1
         do j=1,ihr
            do i=1,n1
#if (PARALLEL == 0)
c              tmpxrcv1=a(i,       j,k)
c              tmpxrcv2=a(i,m1-ihr+j,k)
c              a(i,  m1+j,k)=tmpxrcv1
c              a(i,-ihr+j,k)=tmpxrcv2
               a(i,  m1+j,k)=a(i,       j,k)
               a(i,-ihr+j,k)=a(i,m1-ihr+j,k)
#else
               tmpxsnd1(icnt)=a(i,       j,k)
               tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
               icnt=icnt+1
#endif
            end do
         end do
      end do

#if (PARALLEL > 0)
c
c     exchange data now
c
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()
#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      call shmem_get32(tmpxrcv1,tmpxsnd1,nhl,peabove)
      call shmem_get32(tmpxrcv2,tmpxsnd2,nhl,pebelow)
#endif
#if (SGI_O2K == 2)
      call shmem_get64(tmpxrcv1,tmpxsnd1,nhl,peabove)
      call shmem_get64(tmpxrcv2,tmpxsnd2,nhl,pebelow)
#endif
#else
      call shmem_get(tmpxrcv1,tmpxsnd1,nhl,peabove)
      call shmem_get(tmpxrcv2,tmpxsnd2,nhl,pebelow)
#endif
#endif
#if (PARALLEL == 2)
C#if (SGI_O2K == 0)
C      call mybarrier()
C#endif
#if (ISEND == 3)
      call MPI_ISEND(tmpxsnd1,nhl,DC_TYPE,pebelow,31,MPI_COMM_EULAG,
     .               stats,ierr)
      call MPI_IRECV(tmpxrcv1,nhl,DC_TYPE,peabove,31,MPI_COMM_EULAG,
     .               statr,ierr)
      call MPI_WAIT(stats,status,ierr)
      call MPI_WAIT(statr,status,ierr)

      call MPI_ISEND(tmpxsnd2,nhl,DC_TYPE,peabove,32,MPI_COMM_EULAG,
     .               stats,ierr)
      call MPI_IRECV(tmpxrcv2,nhl,DC_TYPE,pebelow,32,MPI_COMM_EULAG,
     .               statr,ierr)
      call MPI_WAIT(stats,status,ierr)
      call MPI_WAIT(statr,status,ierr)
#endif
#if (ISEND == 2)
      call MPI_IRECV(tmpxrcv1,nhl,DC_TYPE,peabove,31,MPI_COMM_EULAG,
     .               statr,ierr)
      call  MPI_SEND(tmpxsnd1,nhl,DC_TYPE,pebelow,31,MPI_COMM_EULAG,
     .               ierr)
      call MPI_WAIT(statr,status,ierr)

      call MPI_IRECV(tmpxrcv2,nhl,DC_TYPE,pebelow,32,MPI_COMM_EULAG,
     .               statr,ierr)
      call  MPI_SEND(tmpxsnd2,nhl,DC_TYPE,peabove,32,MPI_COMM_EULAG,
     .               ierr)
      call MPI_WAIT(statr,status,ierr)
#endif
#if (ISEND == 1)
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,31,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,31,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,32,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,32,MPI_COMM_EULAG,
     .                  status,ierr)
#endif
#endif

c
c     store data in main array now
c
      icnt=1
      do k=1,l1
         do j=1,ihr
            do i=1,n1
               a(i,  m1+j,k)=tmpxrcv1(icnt)
               a(i,-ihr+j,k)=tmpxrcv2(icnt)
               icnt=icnt+1
            end do
         end do
      end do

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(73)
#endif
#if (DIAGPLT == 1)
      print *,'OUT UPDATEBT',mype
#endif

      return
      end


      subroutine updatelr(a,n1,m1,l1,ndim,mdim,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      dimension a(1-ih:ndim+ih, 1-ih:mdim+ih,l1)
#if (PARALLEL > 0)
#include "msg.lnk"
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif

#if (DIAGPLT == 1)
      print *,'UPDATELR',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(74)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

      mhl=ihr*m1*l1
c
c-------------------------------------------------------------------
c     prepare left data segments for processor on the left  :tmpysnd1
c     prepare right data segments for processor to the right:tmpysnd2
c-------------------------------------------------------------------
c
      icnt=1
      do j=1,m1
         do i=1,ihr
            do k=1,l1
#if (PARALLEL == 0)
c              tmpyrcv1=a(       i,j,k)
c              tmpyrcv2=a(n1-ihr+i,j,k)
c              a(  n1+i,j,k)=tmpyrcv1
c              a(-ihr+i,j,k)=tmpyrcv2
               a(  n1+i,j,k)=a(       i,j,k)
               a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
#else
               tmpysnd1(icnt)=a(       i,j,k)
               tmpysnd2(icnt)=a(n1-ihr+i,j,k)
               icnt=icnt+1
#endif
            end do
         end do
      end do

#if (PARALLEL > 0)
c
c     exchange data now
c
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()
#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      call shmem_get32(tmpyrcv1,tmpysnd1,mhl,peright)
      call shmem_get32(tmpyrcv2,tmpysnd2,mhl,peleft)
#endif
#if (SGI_O2K == 2)          
      call shmem_get64(tmpyrcv1,tmpysnd1,mhl,peright)
      call shmem_get64(tmpyrcv2,tmpysnd2,mhl,peleft)
#endif
#else
      call shmem_get(tmpyrcv1,tmpysnd1,mhl,peright)
      call shmem_get(tmpyrcv2,tmpysnd2,mhl,peleft)
#endif
#endif
#if (PARALLEL == 2)
C#if (SGI_O2K == 0)
C      call mybarrier()
C#endif
#if (ISEND == 3)
      call MPI_ISEND(tmpysnd1,mhl,DC_TYPE,peleft,33,MPI_COMM_EULAG,
     .               stats,ierr)
      call MPI_IRECV(tmpyrcv1,mhl,DC_TYPE,peright,33,MPI_COMM_EULAG,
     .               statr,ierr)
      call MPI_WAIT(stats,status,ierr)
      call MPI_WAIT(statr,status,ierr)

      call MPI_ISEND(tmpysnd2,mhl,DC_TYPE,peright,34,MPI_COMM_EULAG,
     .               stats,ierr)
      call MPI_IRECV(tmpyrcv2,mhl,DC_TYPE,peleft,34,MPI_COMM_EULAG,
     .               statr,ierr)
      call MPI_WAIT(stats,status,ierr)
      call MPI_WAIT(statr,status,ierr)
#endif
#if (ISEND == 2)
      call MPI_IRECV(tmpyrcv1,mhl,DC_TYPE,peright,33,MPI_COMM_EULAG,
     .               statr,ierr)
      call  MPI_SEND(tmpysnd1,mhl,DC_TYPE,peleft,33,MPI_COMM_EULAG,
     .               ierr)
      call MPI_WAIT(statr,status,ierr)

      call MPI_IRECV(tmpyrcv2,mhl,DC_TYPE,peleft,34,MPI_COMM_EULAG,
     .               statr,ierr)
      call  MPI_SEND(tmpysnd2,mhl,DC_TYPE,peright,34,MPI_COMM_EULAG,
     .               ierr)
      call MPI_WAIT(statr,status,ierr)
#endif
#if (ISEND == 1)
      call MPI_Sendrecv(tmpysnd1,mhl,DC_TYPE,peleft,33,
     .                  tmpyrcv1,mhl,DC_TYPE,peright,33,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpysnd2,mhl,DC_TYPE,peright,34,
     .                  tmpyrcv2,mhl,DC_TYPE,peleft,34,MPI_COMM_EULAG,
     .                  status,ierr)
#endif
#endif
c
c     store data in main array now
c
      icnt=1
      do j=1,m1
         do i=1,ihr
            do k=1,l1
               a(  n1+i,j,k)=tmpyrcv1(icnt)
               a(-ihr+i,j,k)=tmpyrcv2(icnt)
               icnt=icnt+1
            end do
         end do
      end do

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(74)
#endif
#if (DIAGPLT == 1)
      print *,'OUT UPDATELR',mype
#endif

      return
      end


#else /* POLES */

#if (SEMILAG == 1)
      subroutine updatelagr(a,n1,m1,l1,ndim,mdim,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      parameter (l3=l+2*ior)
      dimension a(1-ihlag-ior:ndim+ihlag+ior,
     .            1-ihlag-ior:mdim+ihlag+ior,l1)
#if (PARALLEL > 0)
#include "msg.lnk"
      common /updlagr/ tmpxsnd1((np+2*ior)*ihlag*l3),
     .                 tmpxsnd2((np+2*ior)*ihlag*l3),
     .                 tmpysnd1((mp+2*ior)*ihlag*l3),
     .                 tmpysnd2((mp+2*ior)*ihlag*l3),
     .                 tmpcorsnd1(ihlag*ihlag*l3),
     .                 tmpcorsnd2(ihlag*ihlag*l3),
     .                 tmpcorsnd3(ihlag*ihlag*l3),
     .                 tmpcorsnd4(ihlag*ihlag*l3),
     .                 tmpxrcv1((np+2*ior)*ihlag*l3),
     .                 tmpxrcv2((np+2*ior)*ihlag*l3),
     .                 tmpyrcv1((mp+2*ior)*ihlag*l3),
     .                 tmpyrcv2((mp+2*ior)*ihlag*l3),
     .                 tmpcorrcv1(ihlag*ihlag*l3),
     .                 tmpcorrcv2(ihlag*ihlag*l3),
     .                 tmpcorrcv3(ihlag*ihlag*l3),
     .                 tmpcorrcv4(ihlag*ihlag*l3)
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check


#if (DIAGPLT == 1)
      print *,'UPDATE',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(72)
#endif

      ihr=min(ihlag,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

      if (leftedge.eq.1) then
         illim=1-ior
      else
         illim=1
      end if
      if (rightedge.eq.1) then
         iulim=n1+ior
      else
         iulim=n1
      end if
      if (botedge.eq.1) then
         jllim=1-ior
      else
         jllim=1
      end if
      if (topedge.eq.1) then
         julim=m1+ior
      else
         julim=m1
      end if

      nhl=(iulim-illim+1)*ihr*l1
      mhl=(julim-jllim+1)*ihr*l1
      ihl=(ihr*ihr*l1)

#if (PARALLEL == 0)
      do i=illim,iulim
         do j=1,ihr
            do k=1,l1
               tmpxrcv1=a(ip(i),     j,k)
               tmpxrcv2=a(ip(i),m1+1-j,k)
               a(i, 1-j,k)=tmpxrcv1
               a(i,m1+j,k)=tmpxrcv2
            end do
         end do
      end do

      do i=1,ihr
         do j=jllim,julim
            do k=1,l1
               tmpyrcv1=a(i,j,k)
               tmpyrcv2=a(n1-ihr+i,j,k)
               a(n1+i,j,k)=tmpyrcv1
               a(-ihr+i,j,k)=tmpyrcv2
            end do
         end do
      end do

      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorrcv1=a(ip(n1-ihr+i),     j,k)
               tmpcorrcv2=a(ip(n1-ihr+i),m1+1-j,k)
               tmpcorrcv3=a(ip(i)       ,m1+1-j,k)
               tmpcorrcv4=a(ip(i)       ,     j,k)
               a(i-ihr, 1-j,k)=tmpcorrcv1
               a(i-ihr,m1+j,k)=tmpcorrcv2
               a( n1+i,m1+j,k)=tmpcorrcv3
               a( n1+i, 1-j,k)=tmpcorrcv4
            end do
         end do
      end do

#else
c---------------------------------------------------------
c     prepare bottom segments for processor below:tmpxsnd1
c     prepare top segments for processor above   :tmpxsnd2
c---------------------------------------------------------
      icnt=1
      if (nprocx.eq.1) then
        if (botedge.eq.1) then
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,1-j,k)=a(ip(i),j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else if (topedge.eq.1) then
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 a(i,m1+j,k)=a(ip(i),m1+1-j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

c-------------------------------------------------------------------
c     prepare left data segments for processor on the right:tmpysnd1
c     prepare right data segments for processor on the left:tmpysnd2
c-------------------------------------------------------------------
      if (nprocx.eq.1) then
        do i=1,ihr
           do j=jllim,julim
              do k=1,l1
                 a(n1+i,j,k)=a(i,j,k)
                 a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
              end do
           end do
        end do
      else
        icnt=1
        do i=1,ihr
           do j=jllim,julim
              do k=1,l1
                 tmpysnd1(icnt)=a(i,j,k)
                 tmpysnd2(icnt)=a(n1-ihr+i,j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
c----------------------
c     exchange data now
c----------------------
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if(nprocx.gt.1) then

      call shmem_get32(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get32(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#if (SGI_O2K == 2)
      if(nprocx.gt.1) then

      call shmem_get64(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get64(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#else
      if(nprocx.gt.1) then

      call shmem_get(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif

#endif
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif

      if(nprocy.gt.1) then

      if (botedge.eq.1) then
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else if (topedge.eq.1) then
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

      else !nprocy=1 and nprocx>1 (if nprocx=1 PARALLEL=0 no updates)

      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

      if(nprocx.gt.1) then
      call MPI_Sendrecv(tmpysnd1,mhl,DC_TYPE,peleft,3,
     .                  tmpyrcv1,mhl,DC_TYPE,peright,3,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpysnd2,mhl,DC_TYPE,peright,4,
     .                  tmpyrcv2,mhl,DC_TYPE,peleft,4,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

#endif

c
c     store data in main array now
c
      icnt=1
      if (nprocy.gt.1) then

      if (botedge.eq.1) then  !-------------------BOTTOM--------
        if (nprocx.eq.1) then
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 a(i, 1-j,k)=tmpxrcv2(icnt)       !south pole
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else if (topedge.eq.1) then  !-------------TOP----------
        if (nprocx.eq.1) then
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,    -ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else   !---------------------------------MIDLE---------
        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,  m1+j,k)=tmpxrcv1(icnt)
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif  !-----------------------------------------------

      else !nprocy=1

        do i=illim,iulim
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,       1-j,k)=tmpxrcv2(icnt)  !south pole
                 icnt=icnt+1
              end do
           end do
        end do

      endif

      if (nprocx.gt.1) then
        icnt=1
        do i=1,ihr
           do j=jllim,julim
              do k=1,l1
                 a( n1+i, j,k)=tmpyrcv1(icnt)
                 a(-ihr+i,j,k)=tmpyrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
c---------------------------------------
c     now send and receive corner pieces
c---------------------------------------

      if (nprocx.eq.1) then

      if (topedge.eq.1) then
      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd2(icnt)=a(n1-ihr+i,j,k)
               tmpcorsnd3(icnt)=a(i,j,k)
               a(i-ihr,m1+j,k)=a(ip(n1-ihr+i),m1+1-j,k)
               a(n1+i, m1+j,k)=a(ip(i)      ,m1+1-j,k)
               icnt=icnt+1
            end do
         end do
      end do
      else if (botedge.eq.1) then
      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd1(icnt)=a(n1-ihr+i,m1-ihr+j,k)
               tmpcorsnd4(icnt)=a(i,m1-ihr+j,k)
               a(i-ihr, 1-j,k)=a(ip(n1-ihr+i),j,k)
               a(n1+i,  1-j,k)=a(ip(i)      ,j,k)
               icnt=icnt+1
            end do
         end do
      end do
      else
      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd1(icnt)=a(n1-ihr+i,m1-ihr+j,k)
               tmpcorsnd2(icnt)=a(n1-ihr+i,j,k)
               tmpcorsnd3(icnt)=a(i,j,k)
               tmpcorsnd4(icnt)=a(i,m1-ihr+j,k)
               icnt=icnt+1
            end do
         end do
      end do
      endif

      else   !nprocx=1

      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd1(icnt)=a(n1-ihr+i,m1-ihr+j,k)
               tmpcorsnd2(icnt)=a(n1-ihr+i,j,k)
               tmpcorsnd3(icnt)=a(i,j,k)
               tmpcorsnd4(icnt)=a(i,m1-ihr+j,k)
               icnt=icnt+1
            end do
         end do
      end do

      endif

#if (PARALLEL == 1)
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if (nprocx.eq.1) then

c updates on poles are just done
      call shmem_get32(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get32(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get32(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

      else  !nprocx>1

      if (nprocy.gt.1) then

      call shmem_get32(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get32(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get32(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

c updates poles only
      if (topedge.eq.1) then
      call shmem_get32(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      else if (botedge.eq.1) then
      call shmem_get32(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get32(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)
      endif

      else !nprocy=1

c updates poles only
      call shmem_get32(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get32(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      call shmem_get32(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)

      endif !nprocy

      endif !nprocx
#endif
#if (SGI_O2K == 2)
      if (nprocx.eq.1) then

c updates on poles are just done
      call shmem_get64(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get64(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get64(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

      else  !nprocx>1

      if (nprocy.gt.1) then

      call shmem_get64(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get64(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get64(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

c updates poles only
      if (topedge.eq.1) then
      call shmem_get64(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      else if (botedge.eq.1) then
      call shmem_get64(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get64(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)
      endif

      else !nprocy=1

c updates poles only
      call shmem_get64(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get64(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      call shmem_get64(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)

      endif !nprocy

      endif !nprocx
#endif
#else
      if (nprocx.eq.1) then

c updates on poles are just done
      call shmem_get(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

      else  !nprocx>1

      if (nprocy.gt.1) then

      call shmem_get(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

c updates poles only
      if (topedge.eq.1) then
      call shmem_get(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      else if (botedge.eq.1) then
      call shmem_get(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)
      endif

      else !nprocy=1

c updates poles only
      call shmem_get(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      call shmem_get(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)

      endif !nprocy

      endif !nprocx
#endif
#endif
#if (PARALLEL == 2)
      call mybarrier()
      if (nprocx.eq.1) then

c updates on poles are just done
      if (topedge.eq.1) then
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      else if (botedge.eq.1) then
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
      else
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      endif

      else  !nprocx>1

      if (nprocy.gt.1) then

      if (topedge.eq.1) then
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,peleftabove,19,
     .                  tmpcorrcv2,ihl,DC_TYPE,perightabove,19,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,perightabove,20,
     .                  tmpcorrcv3,ihl,DC_TYPE,peleftabove,20,
     .                  MPI_COMM_EULAG,status,ierr)
      else if (botedge.eq.1) then
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)

      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,perightbelow,21,
     .                  tmpcorrcv4,ihl,DC_TYPE,peleftbelow,21,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,peleftbelow,22,
     .                  tmpcorrcv1,ihl,DC_TYPE,perightbelow,22,
     .                  MPI_COMM_EULAG,status,ierr)
      else
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      endif

      else !nprocy=1

      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,peleftabove,19,
     .                  tmpcorrcv2,ihl,DC_TYPE,perightabove,19,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,perightabove,20,
     .                  tmpcorrcv3,ihl,DC_TYPE,peleftabove,20,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,perightbelow,21,
     .                  tmpcorrcv4,ihl,DC_TYPE,peleftbelow,21,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,peleftbelow,22,
     .                  tmpcorrcv1,ihl,DC_TYPE,perightbelow,22,
     .                  MPI_COMM_EULAG,status,ierr)

      endif !nprocy

      endif !nprocx
#endif

      icnt=1
      if (nprocx.gt.1) then

        if (nprocy.gt.1) then

          if (topedge.eq.1) then
          do i=1,ihr
            do j=1,ihr
              do k=1,l1
                a(i-ihr,    -ihr+j,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+1+ihr-j,k)=tmpcorrcv2(icnt)
                a(n1+i ,m1+1+ihr-j,k)=tmpcorrcv3(icnt)
                a(n1+i ,    -ihr+j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do
          else if (botedge.eq.1) then
          do i=1,ihr
            do j=1,ihr
              do k=1,l1
                a(i-ihr, 1-j,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i ,m1+j,k)=tmpcorrcv3(icnt)
                a(n1+i , 1-j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do
          else
          do i=1,ihr
            do j=1,ihr
               do k=1,l1
                a(i-ihr,j-ihr,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+j ,k)=tmpcorrcv2(icnt)
                a(n1+i ,m1+j ,k)=tmpcorrcv3(icnt)
                a(n1+i ,j-ihr,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do
          endif

        else  !nprocy=1

          do i=1,ihr
            do j=1,ihr
              do k=1,l1
                a(i-ihr,       1-j,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+1+ihr-j,k)=tmpcorrcv2(icnt)
                a(n1+i, m1+1+ihr-j,k)=tmpcorrcv3(icnt)
                a(n1+i,        1-j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do

        endif  !nprocy

      else  !nprocx=1

        if (topedge.eq.1) then
         do i=1,ihr
           do j=1,ihr
             do k=1,l1
                a(i-ihr,-ihr+j,k)=tmpcorrcv1(icnt)
                a(n1+i, -ihr+j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
             end do
           end do
         end do
        else if (botedge.eq.1) then
         do i=1,ihr
           do j=1,ihr
             do k=1,l1
                a(i-ihr,m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i, m1+j,k)=tmpcorrcv3(icnt)
                icnt=icnt+1
             end do
           end do
         end do
        else
         do i=1,ihr
           do j=1,ihr
             do k=1,l1
                a(i-ihr,j-ihr,k)=tmpcorrcv1(icnt)
                a(i-ihr, m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i,  m1+j,k)=tmpcorrcv3(icnt)
                a(n1+i, j-ihr,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
             end do
           end do
         end do
        endif

      endif !nprocx

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(72)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATELAG DONE',mype
#endif

      return
      end
#endif

      subroutine updatebtw(a,n1,n2,m1,m2,l1,ndim1,ndim2,mdim1,mdim2,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
#include "msg.lnk"
      dimension a(ndim1-ih:ndim2+ih, mdim1-ih:mdim2+ih,l1)
#if (PARALLEL > 0)
#if (PARALLEL == 2)
#if (SGI_O2K > 0)
      integer stats,statr
#endif
#endif
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check

#if (DIAGPLT == 1)
      print *,'UPDATEW',mype,n1,n2,m1,m2,l1,ndim1,ndim2,mdim1,mdim2,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(70)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

c     nhl=n1*ihr*l1
      nhl=(n2-n1+1)*ihr*l1
      ihl=ihr*ihr*l1

#if (PARALLEL == 0)
      do i=n1,n2    !mod check - modified dim for ip(0) case
c     do i=1,n1
         do j=1,ihr
            do k=1,l1
               tmpxrcv1=a(ip(i),       j,k)
               tmpxrcv2=a(ip(i),m2+1-j,k)
c              tmpxrcv2=a(ip(i),m1+ihr-j,k)
               a(i, m1-j,k)=tmpxrcv1
c              a(i, 1-j,k)=tmpxrcv1
               a(i,m2+j,k)=tmpxrcv2
c              a(i,m1+j,k)=tmpxrcv2
            end do
         end do
      end do

#else /* (PARALLEL == 0) */
c---------------------------------------------------------
c     prepare bottom segments for processor below:tmpxsnd1
c     prepare top segments for processor above   :tmpxsnd2
c---------------------------------------------------------
      icnt=1
      if (nprocx.eq.1) then
        if (botedge.eq.1) then
        do i=n1,n2    !mod check - modified dim for ip(0) case
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1-j,k)=a(ip(n),j,k)
                 a(i,m1-j,k)=a(ip(i),j,k)
c                a(i,1-j,k)=a(ip(i),j,k)
                 tmpxsnd2(icnt)=a(i,m2-ihr+j,k)
c                tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else if (topedge.eq.1) then
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 a(i,m2+j,k)=a(ip(i),m2+1-j,k)
c                a(i,m1+j,k)=a(ip(i),m1+1-j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m2-ihr+j,k)
c                tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m2-ihr+j,k)
c                tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

c----------------------
c     exchange data now
c----------------------
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if(nprocx.gt.1) then

      if(nprocy.gt.1) then
        call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#if (SGI_O2K == 2)
      if(nprocx.gt.1) then

      if(nprocy.gt.1) then
        call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#else      /* SGI_O2K > 0 */
      if(nprocx.gt.1) then

      if(nprocy.gt.1) then
        call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif     /* SGI_O2K > 0 */

#endif     /* PARALLEL == 1 */
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif

      if(nprocy.gt.1) then

      if (botedge.eq.1) then
!     print *,'MPI_Sendrecv A:',nhl,DC_TYPE,peabove,14,13
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
!     print *,'MPI_Sendrecv B:',nhl,DC_TYPE,pebelow,11
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else if (topedge.eq.1) then
!     print *,'MPI_Sendrecv C:',nhl,DC_TYPE,pebelow,13,14
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
!     print *,'MPI_Sendrecv D:',nhl,DC_TYPE,peabove,12
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

      else !nprocy=1 and nprocx>1 (if nprocx=1 PARALLEL=0 no updates)

      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

!     print *,'MPI_Sendrecv F DONE'
#endif   /* PARALLEL == 2 */

c
c     store data in main array now
c
      icnt=1
      if (nprocy.gt.1) then

      if (botedge.eq.1) then  !-------------------BOTTOM--------
        if (nprocx.eq.1) then
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m2+j,k)=tmpxrcv1(icnt)
c                a(i,m1+j,k)=tmpxrcv1(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m2+j,k)=tmpxrcv1(icnt)
c                a(i,m1+j,k)=tmpxrcv1(icnt)
                 a(i, m1-j,k)=tmpxrcv2(icnt)       !south pole
c                a(i, 1-j,k)=tmpxrcv2(icnt)       !south pole
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else if (topedge.eq.1) then  !-------------TOP----------
        if (nprocx.eq.1) then
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1-1-ihr+j,k)=tmpxrcv2(icnt)
c                a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m2+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
c                a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,m1-1-ihr+j,k)=tmpxrcv2(icnt)
c                a(i,    -ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else   !---------------------------------MIDLE---------
        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i, m2+j,k)=tmpxrcv1(icnt)
c                a(i, m1+j,k)=tmpxrcv1(icnt)
                 a(i,m1-1-ihr+j,k)=tmpxrcv2(icnt)
c                a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif  !-----------------------------------------------

      else !nprocy=1

        do i=n1,n2
c       do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m2+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
c                a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,     m1-j,k)=tmpxrcv2(icnt)  !south pole
c                a(i,      1-j,k)=tmpxrcv2(icnt)  !south pole
                 icnt=icnt+1
              end do
           end do
        end do

      endif

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif /* (PARALLEL == 0) */

#if (TIMEPLT == 1)
      call ttend(70)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATE DONE 1',mype
#endif

      return
      end

      subroutine updatelrw(a,n1,n2,m1,m2,l1,ndim1,ndim2,mdim1,mdim2,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
#include "msg.lnk"
      dimension a(ndim1-ih:ndim2+ih, mdim1-ih:mdim2+ih,l1)
#if (PARALLEL > 0)
#if (PARALLEL == 2)
#if (SGI_O2K > 0)
      integer stats,statr
#endif
#endif
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check

#if (DIAGPLT == 1)
      print *,'UPDATEW',mype,n1,n2,m1,m2,l1,ndim1,ndim2,mdim1,mdim2,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(70)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

c     mhl=ihr*m1*l1
      mhl=ihr*(m2-m1+1)*l1
      ihl=ihr*ihr*l1

#if (PARALLEL == 0)
      do i=1,ihr
         do j=m1,m2
c        do j=1,m1
            do k=1,l1
               tmpyrcv1=a(i,j,k)
               tmpyrcv2=a(n2-ihr+i,j,k)
c              tmpyrcv2=a(n1-ihr+i,j,k)
               a(n2+i,j,k)=tmpyrcv1
c              a(n1+i,j,k)=tmpyrcv1
               a(n1-1-ihr+i,j,k)=tmpyrcv2
c              a(-ihr+i,j,k)=tmpyrcv2
            end do
         end do
      end do

#else

c-------------------------------------------------------------------
c     prepare left data segments for processor on the right:tmpysnd1
c     prepare right data segments for processor on the left:tmpysnd2
c-------------------------------------------------------------------
      if (nprocx.eq.1) then
        do i=1,ihr
           do j=m1,m2
c          do j=1,m1
              do k=1,l1
                 a(n2+i,j,k)=a(i,j,k)
c                a(n1+i,j,k)=a(i,j,k)
                 a(n1-1-ihr+i,j,k)=a(n2-ihr+i,j,k)
c                a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
              end do
           end do
        end do
      else
        icnt=1
        do i=1,ihr
           do j=m1,m2
c          do j=1,m1
              do k=1,l1
                 tmpysnd1(icnt)=a(i,j,k)
                 tmpysnd2(icnt)=a(n2-ihr+i,j,k)
c                tmpysnd2(icnt)=a(n1-ihr+i,j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
c----------------------
c     exchange data now
c----------------------
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if(nprocx.gt.1) then

      call shmem_get32(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get32(tmpyrcv2,tmpysnd2,mhl, peleft)

      endif  !nprocx
#endif
#if (SGI_O2K == 2)
      if(nprocx.gt.1) then

      call shmem_get64(tmpyrcv1,tmpysnd1,mhl peright)
      call shmem_get64(tmpyrcv2,tmpysnd2,mhl peleft)

      endif  !nprocx
#endif
#else
      if(nprocx.gt.1) then

      call shmem_get(tmpyrcv1,tmpysnd1,mhl peright)
      call shmem_get(tmpyrcv2,tmpysnd2,mhl peleft)

      endif  !nprocx
#endif

#endif
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif

      if(nprocx.gt.1) then
!     print *,'MPI_Sendrecv E:',mhl,DC_TYPE,peleft,3,peright
      call MPI_Sendrecv(tmpysnd1,mhl,DC_TYPE,peleft,3,
     .                  tmpyrcv1,mhl,DC_TYPE,peright,3,MPI_COMM_EULAG,
     .                  status,ierr)
!     print *,'MPI_Sendrecv F:',mhl,DC_TYPE,peright,4,peleft
      call MPI_Sendrecv(tmpysnd2,mhl,DC_TYPE,peright,4,
     .                  tmpyrcv2,mhl,DC_TYPE,peleft,4,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

!     print *,'MPI_Sendrecv F DONE'
#endif

c
c     store data in main array now
c

      if (nprocx.gt.1) then
        icnt=1
        do i=1,ihr
           do j=m1,m2
c          do j=1,m1
              do k=1,l1
                 a( n2+i,j,k)=tmpyrcv1(icnt)
c                a( n1+i,j,k)=tmpyrcv1(icnt)
                 a(n1-1-ihr+i,j,k)=tmpyrcv2(icnt)
c                a(-ihr+i,j,k)=tmpyrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif /* (PARALLEL == 0) */

#if (TIMEPLT == 1)
      call ttend(70)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATE DONE 2',mype
#endif

      return
      end

      subroutine update(a,n1,m1,l1,ndim,mdim,ihg)

c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      dimension a(1-ih:ndim+ih, 1-ih:mdim+ih,l1)

#if (PARALLEL > 0)
#include "msg.lnk"
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check



#if (DIAGPLT == 1)
      print *,'UPDATE 3',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(70)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big
c     array.
c-------------------------------------------------------------------


      nhl=n1*ihr*l1
      mhl=ihr*m1*l1
      ihl=ihr*ihr*l1

#if (PARALLEL == 0)
      do i=1,n1
         do j=1,ihr
            do k=1,l1
               tmpxrcv1=a(ip(i),       j,k)
               tmpxrcv2=a(ip(i),m1+1-j,k)
c              tmpxrcv2=a(ip(i),m1+ihr-j,k)
               a(i, 1-j,k)=tmpxrcv1
               a(i,m1+j,k)=tmpxrcv2
            end do
         end do
      end do

      do i=1,ihr
         do j=1,m1
            do k=1,l1
               tmpyrcv1=a(i,j,k)
               tmpyrcv2=a(n1-ihr+i,j,k)
               a(n1+i,j,k)=tmpyrcv1
               a(-ihr+i,j,k)=tmpyrcv2
            end do
         end do
      end do

      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorrcv1=a(ip(n1-ihr+i),     j,k)
               tmpcorrcv2=a(ip(n1-ihr+i),m1+1-j,k)
               tmpcorrcv3=a(ip(i)      ,m1+1-j,k)
               tmpcorrcv4=a(ip(i)      ,     j,k)

               a(i-ihr, 1-j,k)=tmpcorrcv1
               a(i-ihr,m1+j,k)=tmpcorrcv2
               a(n1+i,m1+j,k)=tmpcorrcv3
               a(n1+i, 1-j,k)=tmpcorrcv4
            end do
         end do
      end do

#else
c---------------------------------------------------------
c     prepare bottom segments for processor below:tmpxsnd1
c     prepare top segments for processor above   :tmpxsnd2
c---------------------------------------------------------
      icnt=1
      if (nprocx.eq.1) then
        if (botedge.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,1-j,k)=a(ip(i),j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else if (topedge.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 a(i,m1+j,k)=a(ip(i),m1+1-j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

c-------------------------------------------------------------------
c     prepare left data segments for processor on the right:tmpysnd1
c     prepare right data segments for processor on the left:tmpysnd2
c-------------------------------------------------------------------
      if (nprocx.eq.1) then
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 a(n1+i,j,k)=a(i,j,k)
                 a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
              end do
           end do
        end do
      else
        icnt=1
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 tmpysnd1(icnt)=a(i,j,k)
                 tmpysnd2(icnt)=a(n1-ihr+i,j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
c----------------------
c     exchange data now
c----------------------
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if(nprocx.gt.1) then

      call shmem_get32(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get32(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#if (SGI_O2K == 2)
      if(nprocx.gt.1) then

      call shmem_get64(tmpyrcv1,tmpysnd1,mhl peright)
      call shmem_get64(tmpyrcv2,tmpysnd2,mhl peleft)

      if(nprocy.gt.1) then
        call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#else
      if(nprocx.gt.1) then

      call shmem_get(tmpyrcv1,tmpysnd1,mhl peright)
      call shmem_get(tmpyrcv2,tmpysnd2,mhl peleft)

      if(nprocy.gt.1) then
        call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif

#endif
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif

      if(nprocy.gt.1) then

      if (botedge.eq.1) then
!     print *,'MPI_Sendrecv A:',nhl,DC_TYPE,peabove,14,13
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
!     print *,'MPI_Sendrecv B:',nhl,DC_TYPE,pebelow,11
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else if (topedge.eq.1) then
!     print *,'MPI_Sendrecv C:',nhl,DC_TYPE,pebelow,13,14
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
!     print *,'MPI_Sendrecv D:',nhl,DC_TYPE,peabove,12
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

      else !nprocy=1 and nprocx>1 (if nprocx=1 PARALLEL=0 no updates)

      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

      if(nprocx.gt.1) then
!     print *,'MPI_Sendrecv E:',mhl,DC_TYPE,peleft,3,peright
      call MPI_Sendrecv(tmpysnd1,mhl,DC_TYPE,peleft,3,
     .                  tmpyrcv1,mhl,DC_TYPE,peright,3,MPI_COMM_EULAG,
     .                  status,ierr)
!     print *,'MPI_Sendrecv F:',mhl,DC_TYPE,peright,4,peleft
      call MPI_Sendrecv(tmpysnd2,mhl,DC_TYPE,peright,4,
     .                  tmpyrcv2,mhl,DC_TYPE,peleft,4,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

!     print *,'MPI_Sendrecv F DONE'
#endif

c
c     store data in main array now
c
      icnt=1
      if (nprocy.gt.1) then

      if (botedge.eq.1) then  !-------------------BOTTOM--------
        if (nprocx.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 a(i, 1-j,k)=tmpxrcv2(icnt)       !south pole
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else if (topedge.eq.1) then  !-------------TOP----------
        if (nprocx.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,    -ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else   !---------------------------------MIDLE---------
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i, m1+j,k)=tmpxrcv1(icnt)
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif  !-----------------------------------------------

      else !nprocy=1

        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,      1-j,k)=tmpxrcv2(icnt)  !south pole
                 icnt=icnt+1
              end do
           end do
        end do

      endif

      if (nprocx.gt.1) then
        icnt=1
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 a( n1+i,j,k)=tmpyrcv1(icnt)
                 a(-ihr+i,j,k)=tmpyrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
c---------------------------------------
c     now send and receive corner pieces
c---------------------------------------

      if (nprocx.eq.1) then   ! n=mp

      if (topedge.eq.1) then
      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd2(icnt)=a(n1-ihr+i,j,k)
               tmpcorsnd3(icnt)=a(i,j,k)
               a(i-ihr,m1+j,k)=a(ip(n1-ihr+i),m1+1-j,k)
               a(n1+i,m1+j,k)=a(ip(i)      ,m1+1-j,k)
               icnt=icnt+1
            end do
         end do
      end do
      else if (botedge.eq.1) then
      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd1(icnt)=a(n1-ihr+i,m1-ihr+j,k)
               tmpcorsnd4(icnt)=a(i,m1-ihr+j,k)
               a(i-ihr, 1-j,k)=a(ip(n1-ihr+i),j,k)
               a(n1+i, 1-j,k)=a(ip(i)      ,j,k)
               icnt=icnt+1
            end do
         end do
      end do
      else
      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd1(icnt)=a(n1-ihr+i,m1-ihr+j,k)
               tmpcorsnd2(icnt)=a(n1-ihr+i,j,k)
               tmpcorsnd3(icnt)=a(i,j,k)
               tmpcorsnd4(icnt)=a(i,m1-ihr+j,k)
               icnt=icnt+1
            end do
         end do
      end do
      endif

      else   !nprocx=1

      icnt=1
      do i=1,ihr
         do j=1,ihr
            do k=1,l1
               tmpcorsnd1(icnt)=a(n1-ihr+i,m1-ihr+j,k)
               tmpcorsnd2(icnt)=a(n1-ihr+i,j,k)
               tmpcorsnd3(icnt)=a(i,j,k)
               tmpcorsnd4(icnt)=a(i,m1-ihr+j,k)
               icnt=icnt+1
            end do
         end do
      end do

      endif

#if (PARALLEL == 1)
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if (nprocx.eq.1) then

c updates on poles are just done
      call shmem_get32(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get32(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get32(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

      else  !nprocx>1

      if (nprocy.gt.1) then

      call shmem_get32(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get32(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get32(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

c updates poles only
      if (topedge.eq.1) then
      call shmem_get32(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      else if (botedge.eq.1) then
      call shmem_get32(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get32(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)
      endif

      else !nprocy=1

c updates poles only
      call shmem_get32(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get32(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get32(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      call shmem_get32(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)

      endif !nprocy

      endif !nprocx
#endif
#if (SGI_O2K == 2)
      if (nprocx.eq.1) then

c updates on poles are just done
      call shmem_get64(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get64(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get64(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

      else  !nprocx>1

      if (nprocy.gt.1) then

      call shmem_get64(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get64(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get64(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

c updates poles only
      if (topedge.eq.1) then
      call shmem_get64(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      else if (botedge.eq.1) then
      call shmem_get64(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get64(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)
      endif

      else !nprocy=1

c updates poles only
      call shmem_get64(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get64(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get64(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      call shmem_get64(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)

      endif !nprocy

      endif !nprocx
#endif
#else
      if (nprocx.eq.1) then

c updates on poles are just done
      call shmem_get(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

      else  !nprocx>1

      if (nprocy.gt.1) then

      call shmem_get(tmpcorrcv1,tmpcorsnd1,ihl, peleftbelow)
      call shmem_get(tmpcorrcv2,tmpcorsnd2,ihl, peleftabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd3,ihl, perightabove)
      call shmem_get(tmpcorrcv4,tmpcorsnd4,ihl, perightbelow)

c updates poles only
      if (topedge.eq.1) then
      call shmem_get(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      else if (botedge.eq.1) then
      call shmem_get(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)
      endif

      else !nprocy=1

c updates poles only
      call shmem_get(tmpcorrcv1,tmpcorsnd2,ihl, perightbelow)
      call shmem_get(tmpcorrcv2,tmpcorsnd1,ihl, perightabove)
      call shmem_get(tmpcorrcv3,tmpcorsnd4,ihl, peleftabove)
      call shmem_get(tmpcorrcv4,tmpcorsnd3,ihl, peleftbelow)

      endif !nprocy

      endif !nprocx
#endif
#endif
#if (PARALLEL == 2)
      call mybarrier()

      if (nprocx.eq.1) then

c updates on poles are just done
      if (topedge.eq.1) then
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      else if (botedge.eq.1) then
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
      else
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      endif

      else  !nprocx>1

      if (nprocy.gt.1) then

      if (topedge.eq.1) then
!     print *,'MPI_Sendrecv G:',ihl,perightbelow,16,peleftbelow
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
!     print *,'MPI_Sendrecv H:',ihl,peleftbelow,17,perightbelow
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
!     print *,'MPI_Sendrecv I:',ihl,peleftabove,19,perightabove
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,peleftabove,19,
     .                  tmpcorrcv2,ihl,DC_TYPE,perightabove,19,
     .                  MPI_COMM_EULAG,status,ierr)
!     print *,'MPI_Sendrecv J:',ihl,perightabove,20,peleftabove
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,perightabove,20,
     .                  tmpcorrcv3,ihl,DC_TYPE,peleftabove,20,
     .                  MPI_COMM_EULAG,status,ierr)
      else if (botedge.eq.1) then
!     print *,'MPI_Sendrecv K:',ihl,perightabove,15,peleftabove
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
!     print *,'MPI_Sendrecv L:',ihl,peleftabove,18,perightabove
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
!     print *,'MPI_Sendrecv M:',ihl,perightbelow,21,peleftbelow
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,perightbelow,21,
     .                  tmpcorrcv4,ihl,DC_TYPE,peleftbelow,21,
     .                  MPI_COMM_EULAG,status,ierr)
!     print *,'MPI_Sendrecv N:',ihl,peleftbelow,22,perightbelow
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,peleftbelow,22,
     .                  tmpcorrcv1,ihl,DC_TYPE,perightbelow,22,
     .                  MPI_COMM_EULAG,status,ierr)
      else
      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,perightabove,15,
     .                  tmpcorrcv1,ihl,DC_TYPE,peleftbelow,15,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,perightbelow,16,
     .                  tmpcorrcv2,ihl,DC_TYPE,peleftabove,16,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,peleftbelow,17,
     .                  tmpcorrcv3,ihl,DC_TYPE,perightabove,17,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,peleftabove,18,
     .                  tmpcorrcv4,ihl,DC_TYPE,perightbelow,18,
     .                  MPI_COMM_EULAG,status,ierr)
      endif

      else !nprocy=1

      call MPI_Sendrecv(tmpcorsnd1,ihl,DC_TYPE,peleftabove,19,
     .                  tmpcorrcv2,ihl,DC_TYPE,perightabove,19,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd4,ihl,DC_TYPE,perightabove,20,
     .                  tmpcorrcv3,ihl,DC_TYPE,peleftabove,20,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd3,ihl,DC_TYPE,perightbelow,21,
     .                  tmpcorrcv4,ihl,DC_TYPE,peleftbelow,21,
     .                  MPI_COMM_EULAG,status,ierr)
      call MPI_Sendrecv(tmpcorsnd2,ihl,DC_TYPE,peleftbelow,22,
     .                  tmpcorrcv1,ihl,DC_TYPE,perightbelow,22,
     .                  MPI_COMM_EULAG,status,ierr)

      endif !nprocy

      endif !nprocx
!     print *,'MPI_Sendrecv P DONE:'
#endif

      icnt=1
      if (nprocx.gt.1) then

        if (nprocy.gt.1) then

          if (topedge.eq.1) then
          do i=1,ihr
            do j=1,ihr
              do k=1,l1
                a(i-ihr,    -ihr+j,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+1+ihr-j,k)=tmpcorrcv2(icnt)
                a(n1+i,m1+1+ihr-j,k)=tmpcorrcv3(icnt)
                a(n1+i,    -ihr+j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do
          else if (botedge.eq.1) then
          do i=1,ihr
            do j=1,ihr
              do k=1,l1
                a(i-ihr, 1-j,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i,m1+j,k)=tmpcorrcv3(icnt)
                a(n1+i, 1-j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do
          else
          do i=1,ihr
            do j=1,ihr
               do k=1,l1
                a(i-ihr,j-ihr,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i,m1+j,k)=tmpcorrcv3(icnt)
                a(n1+i,j-ihr,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do
          endif

        else  !nprocy=1

          do i=1,ihr
            do j=1,ihr
              do k=1,l1
                a(i-ihr,      1-j,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+1+ihr-j,k)=tmpcorrcv2(icnt)
                a(n1+i,m1+1+ihr-j,k)=tmpcorrcv3(icnt)
                a(n1+i,      1-j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
              end do
            end do
          end do

        endif  !nprocy

      else  !nprocx=1

        if (topedge.eq.1) then
         do i=1,ihr
           do j=1,ihr
             do k=1,l1
                a(i-ihr,-ihr+j,k)=tmpcorrcv1(icnt)
                a(n1+i,-ihr+j,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
             end do
           end do
         end do
        else if (botedge.eq.1) then
         do i=1,ihr
           do j=1,ihr
             do k=1,l1
                a(i-ihr,m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i,m1+j,k)=tmpcorrcv3(icnt)
                icnt=icnt+1
             end do
           end do
         end do
        else
         do i=1,ihr
           do j=1,ihr
             do k=1,l1
                a(i-ihr,j-ihr,k)=tmpcorrcv1(icnt)
                a(i-ihr,m1+j,k)=tmpcorrcv2(icnt)
                a(n1+i,m1+j,k)=tmpcorrcv3(icnt)
                a(n1+i,j-ihr,k)=tmpcorrcv4(icnt)
                icnt=icnt+1
             end do
           end do
         end do
        endif

      endif !nprocx

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(70)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATE DONE 3',mype
#endif

      return
      end


      subroutine update2(a,n1,m1,l1,ndim,mdim,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      dimension a(1-ih:ndim+ih, 1-ih:mdim+ih,l1)
#if (PARALLEL > 0)
#include "msg.lnk"
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check
#if (DIAGPLT == 1)
       print *,'UPDATE2',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(71)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

      nhl=n1*ihr*l1
      mhl=m1*ihr*l1
c
c-------------------------------------------------------------------
c     prepare bottom segments for processor below: tmpxsnd1
c     prepare top segments for processor above   : tmpxsnd2
c-------------------------------------------------------------------
c
      icnt=1
#if (PARALLEL == 0)
      do i=1,n1
         do j=1,ihr
            do k=1,l1
               tmpxrcv1=a(ip(i),     j,k)
               tmpxrcv2=a(ip(i),m1+1-j,k)
               a(i, 1-j,k)=tmpxrcv1
               a(i,m1+j,k)=tmpxrcv2
            end do
         end do
      end do

      do i=1,ihr
         do j=1,m1
            do k=1,l1
               tmpyrcv1=a(i,j,k)
               tmpyrcv2=a(n1-ihr+i,j,k)
               a(n1+i,j,k)=tmpyrcv1
               a(-ihr+i,j,k)=tmpyrcv2
c               a(n1+i,j,k)=a(i,j,k)
c               a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
            end do
         end do
      end do
#else
      if (nprocx.eq.1) then
        if (botedge.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,1-j,k)=a(ip(i),j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else if (topedge.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 a(i,m1+j,k)=a(ip(i),m1+1-j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

c--------------------------------------------------------------------
c     prepare left data segments for processor on the left  :tmpysnd1
c     prepare right data segments for processor to the right:tmpysnd2
c--------------------------------------------------------------------
      if (nprocx.eq.1) then
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 a(n1+i,j,k)=a(i,j,k)
                 a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
              end do
           end do
        end do
      else
        icnt=1
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 tmpysnd1(icnt)=a(i,j,k)
                 tmpysnd2(icnt)=a(n1-ihr+i,j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
c----------------------
c     exchange data now
c----------------------
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if(nprocx.gt.1) then

      call shmem_get32(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get32(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#if (SGI_O2K == 2)
      if(nprocx.gt.1) then

      call shmem_get64(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get64(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#else
      if(nprocx.gt.1) then

      call shmem_get(tmpyrcv1,tmpysnd1,mhl, peright)
      call shmem_get(tmpyrcv2,tmpysnd2,mhl, peleft)

      if(nprocy.gt.1) then
        call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif

#endif
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif

      if(nprocy.gt.1) then

      if (botedge.eq.1) then !BOTEDGE
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else if (topedge.eq.1) then !TOPEDGE
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      if (nprocx.gt.1) then
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else !MIDDLE
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      endif !DONE

      else !nprocy=1 and nprocx>1 (if nprocx=1 PARALLEL=0 no updates)

      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)

      endif

      if(nprocx.gt.1) then
      call MPI_Sendrecv(tmpysnd1,mhl,DC_TYPE,peleft,3,
     .                  tmpyrcv1,mhl,DC_TYPE,peright,3,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpysnd2,mhl,DC_TYPE,peright,4,
     .                  tmpyrcv2,mhl,DC_TYPE,peleft,4,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
#endif
c
c     store data in main array now
c
      icnt=1
      if (nprocy.gt.1) then
      if (botedge.eq.1) then
        if (nprocx.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 a(i, 1-j,k)=tmpxrcv2(icnt)       !south pole
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else if (topedge.eq.1) then
        if (nprocx.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i,    -ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i, m1+j,k)=tmpxrcv1(icnt)
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

      else !nprocy=1

        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i, 1-j,k)=tmpxrcv2(icnt)       !south pole
                 icnt=icnt+1
              end do
           end do
        end do

      endif

      if (nprocx.gt.1) then
        icnt=1
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 a(n1+i,j,k)=tmpyrcv1(icnt)
                 a(-ihr+i,j,k)=tmpyrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(71)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATE2 DONE',mype
#endif

      return
      end


      subroutine updatebt(a,n1,m1,l1,ndim,mdim,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      dimension a(1-ih:ndim+ih, 1-ih:mdim+ih,l1)
#if (PARALLEL > 0)
#include "msg.lnk"
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check


#if (DIAGPLT == 1)
      print *,'UPDATEBT',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(73)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

      nhl=n1*ihr*l1
c
c-------------------------------------------------------------------
c     prepare bottom segments for processor below    :tmpxsnd1
c     prepare upper data segments for processor above:tmpxsnd2
c-------------------------------------------------------------------
c
      icnt=1
#if (PARALLEL == 0)
      do i=1,n1
         do j=1,ihr
            do k=1,l1
               tmpxrcv1=a(ip(i),     j,k)
               tmpxrcv2=a(ip(i),m1+1-j,k)
               a(i, 1-j,k)=tmpxrcv1
               a(i,m1+j,k)=tmpxrcv2
            end do
         end do
      end do
#else
      if (nprocx.eq.1) then
        if (botedge.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,1-j,k)=a(ip(i),j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else if (topedge.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 a(i,m1+j,k)=a(ip(i),m1+1-j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 tmpxsnd1(icnt)=a(i,j,k)
                 tmpxsnd2(icnt)=a(i,m1-ihr+j,k)
                 icnt=icnt+1
              end do
           end do
        end do
      endif
#endif

#if (PARALLEL > 0)
c
c     exchange data now
c
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      if(nprocx.gt.1) then

      if(nprocy.gt.1) then
        call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get32(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get32(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get32(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get32(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#if (SGI_O2K == 2)
      if(nprocx.gt.1) then

      if(nprocy.gt.1) then
        call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get64(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get64(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get64(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get64(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif
#else
      if(nprocx.gt.1) then

      if(nprocy.gt.1) then
        call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)
c       updates poles only
        if (topedge.eq.1) then
          call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
          else if (botedge.eq.1) then
          call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
        endif
      else !nprocy=1
        call shmem_get(tmpxrcv1,tmpxsnd2,nhl, peabove)
        call shmem_get(tmpxrcv2,tmpxsnd1,nhl, pebelow)
      endif

      else !nprocx=1 and nprocy>1 (if nprocy=1 PARALLEL=0 no updates)

c updates on poles are just done
      call shmem_get(tmpxrcv1,tmpxsnd1,nhl, peabove)
      call shmem_get(tmpxrcv2,tmpxsnd2,nhl, pebelow)

      endif  !nprocx
#endif

#endif
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif

      if(nprocy.gt.1) then

      if (botedge.eq.1) then
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      if(nprocx.gt.1) then
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else if (topedge.eq.1) then
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      if(nprocx.gt.1) then
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)
      endif
      else
      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,13,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,13,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,14,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,14,MPI_COMM_EULAG,
     .                  status,ierr)
      endif

      else !nprocy=1 and nprocx>1 (if nprocx=1 PARALLEL=0 no updates)

      call MPI_Sendrecv(tmpxsnd1,nhl,DC_TYPE,pebelow,11,
     .                  tmpxrcv2,nhl,DC_TYPE,pebelow,11,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpxsnd2,nhl,DC_TYPE,peabove,12,
     .                  tmpxrcv1,nhl,DC_TYPE,peabove,12,MPI_COMM_EULAG,
     .                  status,ierr)

      endif

#endif

c
c     store data in main array now
c
      icnt=1
      if (nprocy.gt.1) then
      if (botedge.eq.1) then
        if (nprocx.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 a(i,1-j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else if (topedge.eq.1) then
        if (nprocx.eq.1) then
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
        endif
      else
        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+j,k)=tmpxrcv1(icnt)
                 a(i,-ihr+j,k)=tmpxrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do
      endif

      else !nprocy=1

        do i=1,n1
           do j=1,ihr
              do k=1,l1
                 a(i,m1+1+ihr-j,k)=tmpxrcv1(icnt)  !north pole
                 a(i, 1-j,k)=tmpxrcv2(icnt)       !south pole
                 icnt=icnt+1
              end do
           end do
        end do

      endif


#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(73)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATEBT DONE',mype
#endif

      return
      end


      subroutine updatelr(a,n1,m1,l1,ndim,mdim,ihg)
c
c     this subroutine updates the halo (or ghost) cells surrounding
c     each processors subgrid
c
      include 'param.nml'
      include 'param.ior'
      include 'msg.inc'
      dimension a(1-ih:ndim+ih, 1-ih:mdim+ih,l1)
#if (PARALLEL > 0)
#include "msg.lnk"
      common /updblk/ tmpxsnd1((np+2)*ih*(l+2)),
     .                tmpxsnd2((np+2)*ih*(l+2)),
     .                tmpysnd1((mp+2)*ih*(l+2)),
     .                tmpysnd2((mp+2)*ih*(l+2)),
     .                tmpcorsnd1(ih*ih*(l+2)),
     .                tmpcorsnd2(ih*ih*(l+2)),
     .                tmpcorsnd3(ih*ih*(l+2)),
     .                tmpcorsnd4(ih*ih*(l+2)),
     .                tmpxrcv1((np+2)*ih*(l+2)),
     .                tmpxrcv2((np+2)*ih*(l+2)),
     .                tmpyrcv1((mp+2)*ih*(l+2)),
     .                tmpyrcv2((mp+2)*ih*(l+2)),
     .                tmpcorrcv1(ih*ih*(l+2)),
     .                tmpcorrcv2(ih*ih*(l+2)),
     .                tmpcorrcv3(ih*ih*(l+2)),
     .                tmpcorrcv4(ih*ih*(l+2))
#endif
      common/cyclpole/ irlyq,irlyt,ip(0:n)    !mod check

#if (DIAGPLT == 1)
      print *,'UPDATELR',mype,n1,m1,l1,ndim,mdim,ihg
#endif
#if (TIMEPLT == 1)
      call ttbeg(74)
#endif

      ihr=min(ih,ihg)

#if (PARALLEL == 1)
      call shmem_set_cache_inv()
#endif

c-------------------------------------------------------------------
c     there are four border segments that must be sent and received.
c     first, we copy from the large array to the tmp arrays for
c     sending. next, we send our four border segments to surrounding
c     processors. then, we receive the four segments from the
c     surrounding processors and we copy that data to the big array.
c-------------------------------------------------------------------

      mhl=ihr*m1*l1

#if (PARALLEL == 0)
      do i=1,ihr
         do j=1,m1
            do k=1,l1
c              tmpyrcv1=a(       i,j,k)
c              tmpyrcv2=a(n1-ihr+i,j,k)
c              a(  n1+i,j,k)=tmpyrcv1
c              a(-ihr+i,j,k)=tmpyrcv2
               a(  n1+i,j,k)=a(       i,j,k)
               a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
            end do
         end do
      end do
#else
      if (nprocx.eq.1) then
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 a(  n1+i,j,k)=a(       i,j,k)
                 a(-ihr+i,j,k)=a(n1-ihr+i,j,k)
              end do
           end do
        end do
      else
c--------------------------------------------------------------------
c     prepare left data segments for processor on the left  :tmpysnd1
c     prepare right data segments for processor to the right:tmpysnd2
c--------------------------------------------------------------------
        icnt=1
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 tmpysnd1(icnt)=a(i,j,k)
                 tmpysnd2(icnt)=a(n1-ihr+i,j,k)
                 icnt=icnt+1
              end do
           end do
        end do
c----------------------
c     exchange data now
c----------------------
#if (PARALLEL == 1)
c     call shmem_udcflush()
      call mybarrier()

#if (SGI_O2K > 0)
#if (SGI_O2K == 1)
      call shmem_get32(tmpyrcv1,tmpysnd1,mhl,peright)
      call shmem_get32(tmpyrcv2,tmpysnd2,mhl,peleft)
#endif
#if (SGI_O2K == 2)
      call shmem_get64(tmpyrcv1,tmpysnd1,mhl,peright)
      call shmem_get64(tmpyrcv2,tmpysnd2,mhl,peleft)
#endif
#else
      call shmem_get(tmpyrcv1,tmpysnd1,mhl,peright)
      call shmem_get(tmpyrcv2,tmpysnd2,mhl,peleft)
#endif
#endif
#if (PARALLEL == 2)
#if (SGI_O2K == 0)
      call mybarrier()
#endif
      call MPI_Sendrecv(tmpysnd1,mhl,DC_TYPE,peleft,3,
     .                  tmpyrcv1,mhl,DC_TYPE,peright,3,MPI_COMM_EULAG,
     .                  status,ierr)
      call MPI_Sendrecv(tmpysnd2,mhl,DC_TYPE,peright,4,
     .                  tmpyrcv2,mhl,DC_TYPE,peleft,4,MPI_COMM_EULAG,
     .                  status,ierr)
#endif
c---------------------------------
c     store data in main array now
c---------------------------------
        icnt=1
        do i=1,ihr
           do j=1,m1
              do k=1,l1
                 a(  n1+i,j,k)=tmpyrcv1(icnt)
                 a(-ihr+i,j,k)=tmpyrcv2(icnt)
                 icnt=icnt+1
              end do
           end do
        end do

      endif


#if (PARALLEL == 1)
      call shmem_clear_cache_inv()
#endif

#endif

#if (TIMEPLT == 1)
      call ttend(74)
#endif
#if (DIAGPLT == 1)
      print *,'UPDATELR DONE',mype
#endif

      return
      end

#endif /* POLES */

